import React, { useState, useEffect } from 'react';

const ChatComponent = () => {
  const [userInput, setUserInput] = useState('');
  const [selectedVoice, setSelectedVoice] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('');
  const [chatMessages, setChatMessages] = useState([]);
  const [voices, setVoices] = useState([]);
  const [languages, setLanguages] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log('Fetching config data from server...');
        const response = await fetch('http://127.0.0.1:5000/config');
        if (response.ok) {
          const data = await response.json();
          setVoices(Object.entries(data.voices).map(([label, value]) => ({ label, value })));
          setLanguages(Object.entries(data.languages).map(([label, value]) => ({ label, value })));
        } else {
          console.error('Failed to fetch data from the server:', response.statusText);
        }
      } catch (error) {
        console.error('An error occurred while fetching data:', error);
      }
    };
  
    fetchData();
  }, []);

  useEffect(() => {
    console.log('Voices:', voices);
    console.log('Languages:', languages);
  }, [voices, languages]);

  const sendMessage = async () => {
    try {
      // Create a combined message that includes the selected language and the user input
      const combinedInput = `${selectedLanguage}: ${userInput}`;
  
      // Create a new message object for the user's message
      const userMessage = {
        type: 'user',
        text: combinedInput,
      };
  
      // Update the chatMessages state with the user's message
      setChatMessages((prevMessages) => [...prevMessages, userMessage]);
  
      // Prepare the request body by including the combined input
      const requestBody = {
        user_input: combinedInput,
        selected_voice: selectedVoice,
        selected_language: selectedLanguage,
      };
  
      const response = await fetch('http://127.0.0.1:5000/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });
  
      if (response.ok) {
        const responseData = await response.json();
  
        // Create a new message object for the bot's response
        const botMessage = {
          type: 'bot',
          text: responseData.text,
        };
  
        // Update the chatMessages state with the bot's response
        setChatMessages((prevMessages) => [...prevMessages, botMessage]);
  
        // Play the audio (if needed)
        const audio = new Audio('data:audio/mpeg;base64,' + responseData.audio);
        audio.play();
      } else {
        console.error('Failed to send message to the server:', response.statusText);
      }
    } catch (error) {
      console.error('An error occurred while sending the message:', error);
    }

    if (!userInput.trim()) {
        // Optionally, you could provide some feedback to the user here.
        console.log('No input to send');
        return; // Early return if userInput is empty or whitespace
      }
    
      try {
        // The rest of your sendMessage logic...
      } catch (error) {
        console.error('An error occurred:', error);
      }
  };

  return (
    <div>
      <div>
<select id="voices" value={selectedVoice} onChange={(e) => setSelectedVoice(e.target.value)}>
  {voices.map((voice, index) => (
    <option key={index} value={voice.value}>
      {voice.label}
    </option>
  ))}
</select>
<select id="languages" value={selectedLanguage} onChange={(e) => setSelectedLanguage(e.target.value)}>
  {languages.map((language, index) => (
    <option key={index} value={language.value}>
      {language.label}
    </option>
  ))}
</select>
      </div>
      <div>
      <textarea
  value={userInput}
  onChange={(e) => setUserInput(e.target.value)}
  onKeyDown={(e) => {
    // Check if the Enter key is pressed and not holding Shift
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();  // Prevent the default action to avoid a new line
      sendMessage();       // Call the sendMessage function
    }
  }}
/>
<button onClick={sendMessage} disabled={!userInput.trim()}>Send</button>
</div>
    </div>
  );
};

export default ChatComponent;
