import React, { useState, useEffect } from 'react';

const ChatComponent = () => {
  const [userInput, setUserInput] = useState('');
  const [selectedVoice, setSelectedVoice] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('');
  const [chatMessages, setChatMessages] = useState([]);
  const [voices, setVoices] = useState([]);
  const [languages, setLanguages] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log('Fetching config data from server...');
        const response = await fetch('http://127.0.0.1:5000/config');
        if (response.ok) {
          const data = await response.json();
          setVoices(Object.entries(data.voices).map(([label, value]) => ({ label, value })));
          setLanguages(Object.entries(data.languages).map(([label, value]) => ({ label, value })));
        } else {
          console.error('Failed to fetch data from the server:', response.statusText);
        }
      } catch (error) {
        console.error('An error occurred while fetching data:', error);
      }
    };
  
    fetchData();
  }, []);

  useEffect(() => {
    console.log('Voices:', voices);
    console.log('Languages:', languages);
  }, [voices, languages]);

  const sendMessage = async () => {
    try {
      const userMessage = {
        type: 'user',
        text: userInput,
      };
  
      // Reset input field
      setUserInput('');
  
      // Update the chatMessages state with the user's message
      setChatMessages([...chatMessages, userMessage]);

      // Send the user's message to the server
      const requestBody = {
        user_input: userInput,
        selected_voice: selectedVoice,
        selected_language: selectedLanguage,
      };

      const response = await fetch('http://127.0.0.1:5000/chat', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
  },
  body: JSON.stringify(requestBody),
});

if (response.ok) {
    const responseData = await response.json();

    const botMessage = {
      type: 'bot',
      text: responseData.text,
    };

    // Use the function form of the state setter when the new state depends on the old
    setChatMessages(prevMessages => [...prevMessages, userMessage, botMessage]);

    // ... existing code to play audio ...

  } else {
    console.error('Failed to fetch data from the server.');
  }
} catch (error) {
  console.error('An error occurred:', error);
}
};

  const handleKeyDown = (event) => {
    // Check if Enter was pressed without shift key
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault(); // Prevents the default action of enter which is to insert a new line
      sendMessage();
    }
  };

  return (
    <div>
      {/* Removed an empty div here */}
      <div>
        <select id="voices" value={selectedVoice} onChange={(e) => setSelectedVoice(e.target.value)}>
          {voices.map((voice, index) => (
            <option key={index} value={voice.value}>
              {voice.label}
            </option>
          ))}
        </select>
        <select id="languages" value={selectedLanguage} onChange={(e) => setSelectedLanguage(e.target.value)}>
          {languages.map((language, index) => (
            <option key={index} value={language.value}>
              {language.label}
            </option>
          ))}
        </select>
      </div>
      <div>
        <textarea
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          onKeyDown={handleKeyDown} // Added the onKeyDown event listener here
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default ChatComponent;
