import React, { useState, useEffect } from 'react';

const ChatComponent = () => {
  const [userInput, setUserInput] = useState('');
  const [selectedVoice, setSelectedVoice] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('');
  const [chatMessages, setChatMessages] = useState([]);
  const [voices, setVoices] = useState([]);
  const [languages, setLanguages] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log('Fetching config data from server...');
        const response = await fetch('http://127.0.0.1:5000/config');
        console.log('Response received:', response);
        if (response.ok) {
          const data = await response.json();
          console.log('Data from server:', data);
          setVoices(data.voices);
          setLanguages(data.languages);
        } else {
          console.error('Failed to fetch data from the server:', response.status_code);
        }
      } catch (error) {
        console.error('An error occurred while fetching data:', error);
      }
    };
  
    fetchData();
  }, []);

  const sendMessage = async () => {
    try {
      // Create a new message object for the user's message
      const userMessage = {
        type: 'user',
        text: userInput,
      };

      // Update the chatMessages state with the user's message
      setChatMessages([...chatMessages, userMessage]);

      // Send the user's message to the server
      const requestBody = {
        user_input: userInput,
        selected_voice: selectedVoice,
        selected_language: selectedLanguage,
      };

      const response = await fetch('http://127.0.0.1:5000/chat', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
  },
  body: JSON.stringify(requestBody),
});

      if (response.ok) {
        const responseData = await response.json();

        // Create a new message object for the bot's response
        const botMessage = {
          type: 'bot',
          text: responseData.text,
        };

        // Update the chatMessages state with the bot's response
        setChatMessages([...chatMessages, botMessage]);

        // Play the audio (if needed)
        const audio = new Audio('data:audio/mpeg;base64,' + responseData.audio);
        audio.play();
      } else {
        console.error('Failed to fetch data from the server.');
      }
    } catch (error) {
      console.error('An error occurred:', error);
    }
  };

  return (
    <div>
      <div>
      <select id="voices" value={selectedVoice} onChange={(e) => setSelectedVoice(e.target.value)}>
  {Array.isArray(voices) && voices.map((voice, index) => (
    <option key={index} value={voice.value}>
      {voice.label}
    </option>
  ))}
</select>
<select id="languages" value={selectedLanguage} onChange={(e) => setSelectedLanguage(e.target.value)}>
  {Array.isArray(languages) && languages.map((language, index) => (
    <option key={index} value={language.value}>
      {language.label}
    </option>
  ))}
</select>
      </div>
      <div>
  <textarea
    value={userInput}
    onChange={(e) => setUserInput(e.target.value)}
  />
  <button onClick={sendMessage}>Send</button>
</div>
    </div>
  );
};

export default ChatComponent;
